changeSet.sql:
CREATE SEQUENCE item_id_seq START WITH 1 INCREMENT BY 50; CREATE SEQUENCE shop_id_seq START WITH 1 INCREMENT BY 50; CREATE SEQUENCE shop_order_id_seq START WITH 1 INCREMENT BY 50; CREATE SEQUENCE shop_user_id_seq START WITH 1 INCREMENT BY 50; CREATE SEQUENCE shop_owner_id_seq START WITH 1 INCREMENT BY 50;
CREATE SEQUENCE SHOP_USER_SEQ START WITH 1 INCREMENT BY 50;
CREATE TABLE shop_user ( -- id BIGINT NOT NULL PRIMARY KEY DEFAULT NEXTVAL('shop_user_id_seq'), id BIGINT AUTO_INCREMENT PRIMARY KEY, email_id TEXT NOT NULL UNIQUE, name TEXT NOT NULL, mobile_number TEXT NOT NULL UNIQUE, password TEXT NOT NULL -- is_admin BOOLEAN NOT NULL DEFAULT false );
-- CREATE TABLE shop_owner -- ( -- -- id BIGINT NOT NULL PRIMARY KEY DEFAULT NEXTVAL('shop_user_id_seq'), -- id BIGINT AUTO_INCREMENT PRIMARY KEY, -- email_id TEXT NOT NULL UNIQUE, -- name TEXT NOT NULL, -- mobile_number TEXT NOT NULL UNIQUE, -- password TEXT NOT NULL -- -- is_admin BOOLEAN NOT NULL DEFAULT false -- );
create table shop( -- id BIGINT NOT NULL PRIMARY KEY DEFAULT NEXTVAL('shop_id_seq'), id BIGINT AUTO_INCREMENT PRIMARY KEY, name TEXT NOT NULL , address TEXT NOT NULL , owner_id BIGINT NOT NULL REFERENCES shop_user(id) );
create table item ( -- id BIGINT NOT NULL PRIMARY KEY DEFAULT NEXTVAL('item_id_seq'), id BIGINT AUTO_INCREMENT PRIMARY KEY, name TEXT NOT NULL, description TEXT, quantity BIGINT NOT NULL, price DOUBLE PRECISION NOT NULL , shop_id BIGINT NOT NULL REFERENCES shop(id), image_url TEXT NOT NULL );
create table shop_order( -- id BIGINT NOT NULL PRIMARY KEY DEFAULT NEXTVAL('shop_order_id_seq') , id BIGINT AUTO_INCREMENT PRIMARY KEY, shop_id BIGINT NOT NULL REFERENCES shop(id), user_id BIGINT NOT NULL REFERENCES shop_user(id), item_pojo TEXT NOT NULL, timestamp TIMESTAMP NOT NULL );
