            CREATE SEQUENCE item_id_seq START WITH 1 INCREMENT BY 50;
            CREATE SEQUENCE shop_id_seq START WITH 1 INCREMENT BY 50;
--             CREATE SEQUENCE shop_order_id_seq START WITH 1 INCREMENT BY 50;
--             CREATE SEQUENCE shop_user_id_seq START WITH 1 INCREMENT BY 50;
            CREATE SEQUENCE shop_owner_id_seq START WITH 1 INCREMENT BY 50;
