            CREATE SEQUENCE ITEM_SEQ START WITH 50 INCREMENT BY 50;

            CREATE TABLE shopkeeper
            (
--                 id BIGINT NOT NULL PRIMARY KEY DEFAULT NEXTVAL('shop_user_id_seq'),
                id BIGINT AUTO_INCREMENT PRIMARY KEY,
                email_id TEXT NOT NULL UNIQUE,
                name TEXT NOT NULL,
                mobile_number TEXT NOT NULL UNIQUE,
                password TEXT NOT NULL
--                 is_admin BOOLEAN NOT NULL DEFAULT false
            );


            create table shop(
--                                  id BIGINT NOT NULL PRIMARY KEY DEFAULT NEXTVAL('shop_id_seq'),
                                 id BIGINT AUTO_INCREMENT PRIMARY KEY,
                                 name TEXT NOT NULL ,
                                 address TEXT NOT NULL ,
                                 owner_id BIGINT NOT NULL REFERENCES shopkeeper(id)
            );

            create table item
            (
--                 id   BIGINT NOT NULL PRIMARY KEY DEFAULT NEXTVAL('item_id_seq'),
                id BIGINT AUTO_INCREMENT PRIMARY KEY,
                name TEXT   NOT NULL,
                description TEXT,
                quantity BIGINT NOT NULL,
                price DOUBLE PRECISION NOT NULL ,
                shop_id BIGINT NOT NULL REFERENCES shop(id),
                image_url TEXT NOT NULL,
                archived BOOLEAN NOT NULL DEFAULT false
            );

            create table address
            (
                id BIGINT AUTO_INCREMENT PRIMARY KEY,
                address_type TEXT NOT NULL,
                addressable_id BIGINT NOT NULL,
                address_type_name TEXT NOT NULL,
                street TEXT NOT NULL,
                city TEXT NOT NULL,
                state TEXT NOT NULL,
                postal_code TEXT NOT NULL
            );
